mapper.select:
select no, title, nickname, content, to_char(reg_date, 'yyyy-mm-dd') as regDate, view_cnt as viewCnt, recommend_cnt as recommendCnt
  from ssh_build
 where user_no = #{no}

mapper.update:
update ssh_build
   set title = #{title}, content = #{content}
 where no = #{no}

update ssh_build
   set view_cnt = view_cnt + 1
 where no = #{no}

update ssh_build
   set recommend_cnt = recommend_cnt + 1
 where no = #{no}
